changeSet.sql:
create table GROUPS
(
ID              bigserial    not null,
NAME            text         not null,
DATE_TIME       timestamp    not null,
PLACES_COUNT    integer      not null,
BOOKED          integer,
SCHEDULE_ID     bigserial    not null
);

create unique index GROUPS_uindex on GROUPS (ID);

alter table GROUPS add constraint GROUPS_pk primary key (ID);

COMMENT ON TABLE GROUPS IS 'Groups table';

COMMENT ON COLUMN GROUPS.ID             IS 'Group id';
COMMENT ON COLUMN GROUPS.NAME           IS 'Group name';
COMMENT ON COLUMN GROUPS.DATE_TIME      IS 'Group date time';
COMMENT ON COLUMN GROUPS.PLACES_COUNT   IS 'Group places count';
COMMENT ON COLUMN GROUPS.BOOKED         IS 'Group booked places count';
COMMENT ON COLUMN GROUPS.SCHEDULE_ID    IS 'Group schedule id';
