changeSet.sql:
create table SCHEDULES
(
ID              bigserial    not null,
NAME            text         not null
);

create unique index SCHEDULES_uindex on SCHEDULES (ID);

alter table SCHEDULES add constraint SCHEDULES_pk primary key (ID);

COMMENT ON TABLE SCHEDULES IS 'Schedules table';

COMMENT ON COLUMN SCHEDULES.ID             IS 'Schedule id';
COMMENT ON COLUMN SCHEDULES.NAME           IS 'Schedule name';
