changeSet.sql:
create table STUDENTS
(
ID              bigserial    not null,
NAME            text         not null,
PHONE           text         not null
);

create unique index STUDENTS_uindex on STUDENTS (ID);

alter table STUDENTS add constraint STUDENTS_pk primary key (ID);

COMMENT ON TABLE STUDENTS IS 'Students table';

COMMENT ON COLUMN STUDENTS.ID             IS 'Students id';
COMMENT ON COLUMN STUDENTS.NAME           IS 'Students name';
COMMENT ON COLUMN STUDENTS.PHONE          IS 'Students phone';
